 available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Mapa</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"nl_NL\">Kaart</Name><Name language-id=\"hu_HU\">Trkp</Name><Name language-id=\"pt_BR\">Mapa</Name><Name language-id=\"de_DE\">Landkarte</Name><Name language-id=\"iw_IL\"></Name><Name language-id=\"fi_FI\">Kartta</Name><Name language-id=\"ca_ES\">Mapa</Name><Name language-id=\"fr_FR\">Carte</Name><Name language-id=\"en_US\">Map</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra activos geolocalizados en un mapa.</Description><Description language-id=\"ja_JP\"></Description><Description language-id=\"nl_NL\">Toon geolocale content op een kaart.</Description><Description language-id=\"hu_HU\">Megjelenti a fldrajzi tartalmakat egy trkpen.</Description><Description language-id=\"pt_BR\">Exibe os contedos geolocalizados em um mapa.</Description><Description language-id=\"de_DE\">Stellt Assets mit geografischen Informationen in einer Karte dar.</Description><Description language-id=\"iw_IL\">    -   .</Description><Description language-id=\"fi_FI\">Nytt maantieteelliset sisllt kartalla.</Description><Description language-id=\"ca_ES\">Mostra contingut geolocalitzat en un mapa.</Description><Description language-id=\"fr_FR\">Affiche les assets golocaliss dans un plan</Description><Description language-id=\"en_US\">Displays geolocalized assets in a map.</Description></root>','ftl','<#assign defaultLatitude = -3.6833 />\n<#assign defaultLongitude = 40.40 />\n\n<#assign group = themeDisplay.getScopeGroup() />\n\n<#assign mapsAPIProvider = group.getLiveParentTypeSettingsProperty(\"mapsAPIProvider\")!\"\" />\n\n<#assign companyPortletPreferences = prefsPropsUtil.getPreferences(companyId) />\n\n<#if mapsAPIProvider = \"\">\n	<#assign mapsAPIProvider = companyPortletPreferences.getValue(\"mapsAPIProvider\", \"Google\") />\n</#if>\n\n<#assign featureCollectionJSONObject = jsonFactoryUtil.createJSONObject() />\n\n<@liferay.silently featureCollectionJSONObject.put(\"type\", \"FeatureCollection\") />\n\n<#assign featureJSONArray = jsonFactoryUtil.createJSONArray() />\n\n<#list entries as entry>\n	<#assign assetRenderer = entry.getAssetRenderer() />\n\n	<#assign ddmFormValuesReader = assetRenderer.getDDMFormValuesReader() />\n\n	<#assign ddmFormFieldValues = ddmFormValuesReader.getDDMFormFieldValues(\"ddm-geolocation\") />\n\n	<#assign coordinatesJSONObjects = [] />\n\n	<#list ddmFormFieldValues as ddmFormFieldValue>\n		<#assign value = ddmFormFieldValue.getValue() />\n\n		<#assign coordinatesJSONObject = jsonFactoryUtil.createJSONObject(value.getString(locale)) />\n\n		<#assign coordinatesJSONObjects = coordinatesJSONObjects + [coordinatesJSONObject] />\n	</#list>\n\n	<#list coordinatesJSONObjects as coordinatesJSONObject>\n		<#assign featureJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently featureJSONObject.put(\"type\", \"Feature\") />\n\n		<#assign geometryJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently geometryJSONObject.put(\"type\", \"Point\") />\n\n		<#assign coordinatesJSONArray = [coordinatesJSONObject.getDouble(\"longitude\"), coordinatesJSONObject.getDouble(\"latitude\")] />\n\n		<@liferay.silently geometryJSONObject.put(\"coordinates\", coordinatesJSONArray) />\n\n		<@liferay.silently featureJSONObject.put(\"geometry\", geometryJSONObject) />\n\n		<#assign propertiesJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently propertiesJSONObject.put(\"title\", assetRenderer.getTitle(locale)) />\n\n		<#assign entryAbstract>\n			<@getAbstract asset=entry />\n		</#assign>\n\n		<@liferay.silently propertiesJSONObject.put(\"abstract\", entryAbstract) />\n\n		<#if mapsAPIProvider = \"Google\">\n			<#assign\n				images = {\n					\"com.liferay.document.library.kernel.model.DLFileEntry\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/green-dot.png\",\n					\"com.liferay.portlet.dynamicdatalists.model.DDLRecord\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/red-dot.png\",\n					\"com.liferay.journal.model.JournalArticle\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/blue-dot.png\",\n					\"default\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/yellow-dot.png\"\n				}\n			/>\n\n			<#if images?keys?seq_contains(entry.getClassName())>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[entry.getClassName()]) />\n			<#else>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[\"default\"]) />\n			</#if>\n		</#if>\n\n		<@liferay.silently featureJSONObject.put(\"properties\", propertiesJSONObject) />\n\n		<@liferay.silently featureJSONArray.put(featureJSONObject) />\n	</#list>\n</#list>\n\n<@liferay.silently featureCollectionJSONObject.put(\"features\", featureJSONArray) />\n\n<style type=\"text/css\">\n	.asset-entry-abstract {\n		min-width: 400px;\n	}\n\n	.leaflet-popup .asset-entry-abstract {\n		display: inline-block;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image {\n		display: block;\n		float: left;\n		height: 128px;\n		margin-right: 1em;\n		text-align: center;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image img {\n		display: block;\n		margin: 0 auto;\n		max-height: 100%;\n	}\n\n	.asset-entry-abstract .taglib-icon {\n		float: right;\n	}\n</style>\n\n<@liferay_map[\"map-display\"]\n	name=\'Map\'\n	points=\"${featureCollectionJSONObject}\"\n/>\n\n<@liferay_aui.script use=\"liferay-map-base\">\n	var map = Liferay.component(\'<@portlet.namespace />Map\');\n\n	map.on(\n		\'featureClick\',\n		function(event) {\n			var feature = event.feature;\n\n			map.openDialog(\n				{\n					content: feature.getProperty(\'abstract\'),\n					marker: feature.getMarker(),\n					position: feature.getGeometry().get(\'location\')\n				}\n			);\n		}\n	);\n</@liferay_aui.script>\n\n<#macro getAbstract asset>\n	<div class=\"asset-entry-abstract\" id=\"<@portlet.namespace />assetEntryAbstract\">\n		<#assign showEditURL = paramUtil.getBoolean(renderRequest, \"showEditURL\", true) />\n\n		<#assign assetRenderer = asset.getAssetRenderer() />\n\n		<#if showEditURL && assetRenderer.hasEditPermission(permissionChecker)>\n			<#assign redirectURL = renderResponse.createLiferayPortletURL(themeDisplay.getPlid(), themeDisplay.getPortletDisplay().getId(), \"RENDER_PHASE\", false) />\n\n			${redirectURL.setParameter(\"mvcPath\", \"/add_asset_redirect.jsp\")}\n\n			<#assign editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"POP_UP\"), redirectURL) />\n\n			<#assign taglibEditURL = \"javascript:Liferay.Util.openWindow({id: \'\" + renderResponse.getNamespace() + \"editAsset\', title: \'\" + htmlUtil.escapeJS(languageUtil.format(locale, \"edit-x\", htmlUtil.escape(assetRenderer.getTitle(locale)), false)) + \"\', uri:\'\" + htmlUtil.escapeJS(editPortletURL.toString()) + \"\'});\" />\n\n			<@liferay_ui.icon\n				image = \"edit\"\n				label = true\n				message = \"edit\"\n				url = taglibEditURL\n			/>\n		</#if>\n\n		<#if assetRenderer.getThumbnailPath(renderRequest)??>\n			<div class=\"asset-entry-abstract-image\">\n				<img src=\"${assetRenderer.getThumbnailPath(renderRequest)}\" />\n			</div>\n		</#if>\n\n		<#assign assetURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, asset) />\n\n		<div class=\"asset-entry-abstract-content\">\n			<h3><a href=\"${assetURL}\">${assetRenderer.getTitle(locale)}</a></h3>\n\n			<div>\n				${assetRenderer.getSummary(renderRequest, renderResponse)}\n			</div>\n		</div>\n\n		<div class=\"asset-entry-abstract-footer\">\n			<a href=\"${assetURL}\">${languageUtil.get(locale, \"read-more\")} &raquo;</a>\n		</div>\n	</div>\n</#macro>',0,20120,NULL,'2017-04-18 12:51:59.929000'),(29094,20160,20116,20120,NULL,'2017-04-18 12:51:59.955000',20006,0,29092,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Resumen completo</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"nl_NL\">Rijke samenvatting</Name><Name language-id=\"hu_HU\">B sszest</Name><Name language-id=\"pt_BR\">Sumrio Rico</Name><Name language-id=\"de_DE\">Rich-Text-Zusammenfassung</Name><Name language-id=\"iw_IL\"> </Name><Name language-id=\"fi_FI\">Muotoiltu yhteenveto</Name><Name language-id=\"ca_ES\">Resum enriquit</Name><Name language-id=\"fr_FR\">Sommaire enrichi</Name><Name language-id=\"en_US\">Rich Summary</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Description language-id=\"zh_CN\"></Description><Description language-id=\"es_ES\">Muestra abstractos, iconos, activos relacionados e imprime/edita acciones para activos. Opcionalmente incluye marcadores de activos y calificaciones.</Description><Description language-id=\"ja_JP\">/</Description><Description language-id=\"nl_NL\">Toon samenvattingen, pictogrammen, gerelateerde content en afdruk en bewerk acties voor content. Voeg optioneel bladwijzers en beoordelingen toe.</Description><Description language-id=\"hu_HU\">Megjelenti a kivonatokat, ikonokat, a kapcsold tartalmakat, s a nyomtatsi/szerkesztsi mveleteket a tarlamakhoz. Opcionlisan tartalmazza a tartalom knyvjelzket s rtkelseket.</Description><Description language-id=\"pt_BR\">Exibe abstracts, cones, contedos relacionados e aes de imprimir/editar para os contedos. Opcionalmente, inclui marcadores e classificaes de contedos.</Description><Description language-id=\"de_DE\">Stellt Kurzfassungen, Icons, verwandte Assets sowie Druck- und Bearbeitungsoptionen fr Assets dar. Kann auch Bookmarks und Bewertungen enthalten.</Description><Description language-id=\"iw_IL\"> , ,    /  .     .</Description><Description language-id=\"fi_FI\">Nytt lyhenteet, kuvakkeet, liittyvt sisllt ja sisltjen tulosta/muokkaa toiminnot. Voit ottaa mukaan mys sisltjen kirjanmerkit ja arvostelut.</Description><Description language-id=\"ca_ES\">Mostra resums, icones, continguts relacionats i accions d\'impressi o edici per continguts. Opcionalment, inclou enllaos i valoracions.</Description><Description language-id=\"fr_FR\">Affiche les rsums, icons, les assets relatifs, et les actions imprimer/diter pour les assets. Facultativement inclut les signets et les notations d\'assets.</Description><Description language-id=\"en_US\">Displays abstracts, icons, related assets, and print/edit actions for assets. Optionally include asset bookmarks and ratings.</Description></root>','ftl','<#if !entries?has_content>\n	<#if !themeDisplay.isSignedIn()>\n		${renderRequest.setAttribute(\"PORTLET_CONFIGURATOR_VISIBILITY\", true)}\n	</#if>\n\n	<div class=\"alert alert-info\">\n		<@liferay_ui[\"message\"]\n			key=\"there-are-no-results\"\n		/>\n	</div>\n</#if>\n\n<#list entries as entry>\n	<#assign entry = entry />\n\n	<#assign assetRenderer = entry.getAssetRenderer() />\n\n	<#assign entryTitle = htmlUtil.escape(assetRenderer.getTitle(locale)) />\n\n	<#assign viewURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, entry) />\n\n	<#if assetLinkBehavior != \"showFullContent\">\n		<#assign viewURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, entry, true) />\n	</#if>\n\n	<div class=\"asset-abstract\">\n		<div class=\"pull-right\">\n			<@getPrintIcon />\n\n			<@getFlagsIcon />\n\n			<@getEditIcon />\n		</div>\n\n		<h3 class=\"asset-title\">\n			<a href=\"${viewURL}\">\n				${entryTitle}\n			</a>\n		</h3>\n\n		<@getMetadataField fieldName=\"tags\" />\n\n		<@getMetadataField fieldName=\"create-date\" />\n\n		<@getMetadataField fieldName=\"view-count\" />\n\n		<div class=\"asset-content\">\n			<@getSocialBookmarks />\n\n			<div class=\"asset-summary\">\n				<@getMetadataField fieldName=\"author\" />\n\n				${htmlUtil.escape(assetRenderer.getSummary(renderRequest, renderResponse))}\n\n				<a href=\"${viewURL}\"><@liferay.language key=\"read-more\" /><span class=\"hide-accessible\"><@liferay.language key=\"about\" />${entryTitle}</span> &raquo;</a>\n			</div>\n\n			<@getRatings />\n\n			<@getRelatedAssets />\n\n			<@getDiscussion />\n		</div>\n	</div>\n</#list>\n\n<#macro getDiscussion>\n	<#if getterUtil.getBoolean(enableComments) && assetRenderer.isCommentable()>\n		<br />\n\n		<#assign discussionURL = renderResponse.createActionURL() />\n\n		${discussionURL.setParameter(\"javax.portlet.action\", \"invokeTaglibDiscussion\")}\n\n		<@liferay_ui[\"discussion\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			formAction=discussionURL?string\n			formName=\"fm\" + entry.getClassPK()\n			ratingsEnabled=getterUtil.getBoolean(enableCommentRatings)\n			redirect=currentURL\n			userId=assetRenderer.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getEditIcon>\n	<#if assetRenderer.hasEditPermission(themeDisplay.getPermissionChecker())>\n		<#assign redirectURL = renderResponse.createRenderURL() />\n\n		${redirectURL.setParameter(\"mvcPath\", \"/add_asset_redirect.jsp\")}\n		${redirectURL.setWindowState(\"pop_up\")}\n\n		<#assign editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"pop_up\"), redirectURL)!\"\" />\n\n		<#if validator.isNotNull(editPortletURL)>\n			<#assign title = languageUtil.format(locale, \"edit-x\", entryTitle, false) />\n\n			<@liferay_ui[\"icon\"]\n				cssClass=\"icon-monospaced visible-interaction\"\n				icon=\"pencil\"\n				markupView=\"lexicon\"\n				message=title\n				url=\"javascript:Liferay.Util.openWindow({id:\'\" + renderResponse.getNamespace() + \"editAsset\', title: \'\" + title + \"\', uri:\'\" + htmlUtil.escapeURL(editPortletURL.toString()) + \"\'});\"\n			/>\n		</#if>\n	</#if>\n</#macro>\n\n<#macro getFlagsIcon>\n	<#if getterUtil.getBoolean(enableFlags)>\n		<@liferay_flags[\"flags\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			contentTitle=entry.getTitle(locale)\n			label=false\n			reportedUserId=entry.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getMetadataField\n	fieldName\n>\n	<#if stringUtil.split(metadataFields)?seq_contains(fieldName)>\n		<span class=\"metadata-entry metadata-${fieldName}\">\n			<#assign dateFormat = \"dd MMM yyyy - HH:mm:ss\" />\n\n			<#if fieldName == \"author\">\n				<@liferay.language key=\"by\" /> ${portalUtil.getUserName(assetRenderer.getUserId(), assetRenderer.getUserName())}\n			<#elseif fieldName == \"categories\">\n				<@liferay_ui[\"asset-categories-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif fieldName == \"create-date\">\n				${dateUtil.getDate(entry.getCreateDate(), dateFormat, locale)}\n			<#elseif fieldName == \"expiration-date\">\n				${dateUtil.getDate(entry.getExpirationDate(), dateFormat, locale)}\n			<#elseif fieldName == \"modified-date\">\n				${dateUtil.getDate(entry.getModifiedDate(), dateFormat, locale)}\n			<#elseif fieldName == \"priority\">\n				${entry.getPriority()}\n			<#elseif fieldName == \"publish-date\">\n				${dateUtil.getDate(entry.getPublishDate(), dateFormat, locale)}\n			<#elseif fieldName == \"tags\">\n				<@liferay_ui[\"asset-tags-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif fieldName == \"view-count\">\n				${entry.getViewCount()} <@liferay.language key=\"views\" />\n			</#if>\n		</span>\n	</#if>\n</#macro>\n\n<#macro getPrintIcon>\n	<#if getterUtil.getBoolean(enablePrint)>\n		<#assign printURL = renderResponse.createRenderURL() />\n\n		${printURL.setParameter(\"mvcPath\", \"/view_content.jsp\")}\n		${printURL.setParameter(\"assetEntryId\", entry.getEntryId()?string)}\n		${printURL.setParameter(\"viewMode\", \"print\")}\n		${printURL.setParameter(\"type\", entry.getAssetRendererFactory().getType())}\n\n		<#if assetRenderer.getUrlTitle()?? && validator.isNotNull(assetRenderer.getUrlTitle())>\n			<#if assetRenderer.getGroupId() != themeDisplay.getScopeGroupId()>\n				${printURL.setParameter(\"groupId\", assetRenderer.getGroupId()?string)}\n			</#if>\n\n			${printURL.setParameter(\"urlTitle\", assetRenderer.getUrlTitle())}\n		</#if>\n\n		${printURL.setWindowState(\"pop_up\")}\n\n		<@liferay_ui[\"icon\"]\n			iconCssClass=\"icon-print\"\n			message=\"print\"\n			url=\"javascript:Liferay.Util.openWindow({id:\'\" + renderResponse.getNamespace() + \"printAsset\', title: \'\" + languageUtil.format(locale, \"print-x-x\", [\"hide-accessible\", entryTitle], false) + \"\', uri: \'\" + htmlUtil.escapeURL(printURL.toString()) + \"\'});\"\n		/>\n	</#if>\n</#macro>\n\n<#macro getRatings>\n	<#if getterUtil.getBoolean(enableRatings) && assetRenderer.isRatable()>\n		<div class=\"asset-ratings\">\n			<@liferay_ui[\"ratings\"]\n				className=entry.getClassName()\n				classPK=entry.getClassPK()\n			/>\n		</div>\n	</#if>\n</#macro>\n\n<#macro getRelatedAssets>\n	<#if getterUtil.getBoolean(enableRelatedAssets)>\n		<@liferay_ui[\"asset-links\"]\n			assetEntryId=entry.getEntryId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getSocialBookmarks>\n	<#if getterUtil.getBoolean(enableSocialBookmarks)>\n		<@liferay_ui[\"social-bookmarks\"]\n			displayStyle=\"${socialBookmarksDisplayStyle}\"\n			target=\"_blank\"\n			title=entry.getTitle(locale)\n			url=viewURL\n		/>\n	</#if>\n</#macro>',0,20120,NULL,'2017-04-18 12:51:59.955000'),(29413,20160,20116,20120,NULL,'2017-04-18 12:52:14.713000',28936,29408,2